import React, { useState, useRef } from "react";
import Dropdown from "../../components/dropdown/dropdown";
import Navbar from "../../components/nav/navbar";
import emailjs from "emailjs-com";
import Lottie from "react-lottie";
import confettiData from "../../assets/lotties/confetti2.json";
import Fade from "react-reveal/Fade";
import "./contact.css";

const Confetti = (lottieResult) => {
  const defaultOptions = {
    loop: false,
    autoplay: true,
    animationData: confettiData,
  };
  return (
      <div className={`lottie-bg ${lottieResult ? "active" : ""}`}>
      <Lottie options={defaultOptions} width={"100%"} />
    </div>
  );
};

const MsgDetail = () => {
  return (
    <>
      <Fade>
        <p className="sentMsg">
          Your message has successfully been sent! We will contact you soon.{" "}
        </p>
      </Fade>
    </>
  );
};

const Contact = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  };

  const form = useRef();

  const [result, showResult] = useState(false);
  const [lottieResult, showLottieResult] = useState(false);

  const customSubmit = (e) => {
    e.preventDefault();
    e.target.reset();
    sendEmail(e);
    playConfetti(e);
  };

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "//service_2npcj2s",
        "my-first-templat",
        e.target,
        "user_sA3tlOdalK12Wt9JkvHaZ"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );

    e.target.reset();
    showResult(true);
  };

  const playConfetti = (e) => {
    e.preventDefault();
    showLottieResult(true);
  };

  return (
    <>
      <Dropdown isOpen={isOpen} toggle={toggle} />

      <Navbar toggle={toggle} />

      <div className="contactBody">
        <Fade>
          <div className="contactOverlay">
            <div className="titleBox">
              <div className="titleMsgText">
                <h1> Message me! </h1>
              </div>
            </div>

            <div className="container">
            <div className="form">
  <form action="" onSubmit={customSubmit}>
    <div className="form-row">
      <div className="form-column">
        <div className="form-input-field">
          <span className="form-label"> Name / Company </span>
          <input
            type="text"
            placeholder="Name/Company"
            name="name"
            value="test"
            required
            className="form-input"
          />
        </div>
        <div className="form-input-field">
          <span className="form-label"> Email</span>
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value="test@test.com"
            required
            className="form-input"
          />
        </div>
        <div className="form-input-field">
          <span className="form-label"> Subject </span>
          <input
            type="text"
            placeholder="Subject"
            name="subject"
            value="subject"
            required
            className="form-input"
          />
        </div>
      </div>

      <div className="form-column">
        <div className="form-input-field">
          <span className="form-label"> Message</span>
          <textarea
            placeholder="Your message"
            name="message"
            value="msg"
            required
            className="form-textarea"
          ></textarea>
        </div>
      </div>
    </div>

    <div className="form-row form-btn">
      <button> Send Message </button>
    </div>
  </form>
                  <div className="submittedForm">
                    {result && lottieResult ? (
                      <>
                        <MsgDetail />
                        <Confetti lottieResult={lottieResult}/>
                      </>
                    ) : null}
                  </div>
              </div>
            </div>
          </div>
        </Fade>
      </div>
    </>
  );
};

export default Contact;
